create table waf_user_info(    id          bigint unsigned auto_increment        primary key,    created_at  datetime(3)  null,    updated_at  datetime(3)  null,    deleted_at  datetime(3)  null,    email       varchar(50)  not null comment '''用户邮箱''',    user_name   varchar(20)  null,    password    varchar(20)  not null,    avatar_addr varchar(200) null comment '''头像地址''',    phone       varchar(20)  null comment '''手机号''',    constraint uni_waf_user_info_email        unique (email));create index idx_waf_user_info_deleted_at    on waf_user_info (deleted_at);