create table app_waf(    id         bigint unsigned auto_increment        primary key,    created_at datetime(3)  null,    updated_at datetime(3)  null,    deleted_at datetime(3)  null,    name       varchar(255) not null comment '''应用名称''',    url        varchar(255) null comment '''web程序地址''',    server_id  bigint       not null comment '''web程序所在服务器id''',    constraint uni_app_waf_name        unique (name),    constraint uni_app_waf_server_id        unique (server_id));create index idx_app_waf_deleted_at    on app_waf (deleted_at);