create table server_waf(    id              bigint unsigned auto_increment        primary key,    created_at      datetime(3)       null,    updated_at      datetime(3)       null,    deleted_at      datetime(3)       null,    name            varchar(255)      not null comment '''服务器名称''',    host            varchar(255)      not null comment '''服务器域名''',    ip              varchar(255)      not null comment '''服务器IP地址''',    port            bigint            not null comment '''服务器端口''',    tls             tinyint default 2 not null comment '''是否开启TLS 2否 1是''',    cert            varchar(255)      null comment '''TLS证书''',    `key`           varchar(255)      null comment '''TLS密钥''',    server_group_id bigint  default 0 not null comment '''服务器组ID-预留字段''',    constraint uni_server_waf_host        unique (host),    constraint uni_server_waf_name        unique (name));create index idx_server_waf_deleted_at    on server_waf (deleted_at);