create table waf_strategy(    id          bigint unsigned auto_increment        primary key,    created_at  datetime(3)       null,    updated_at  datetime(3)       null,    deleted_at  datetime(3)       null,    name        varchar(255)      not null comment '''策略名称''',    description varchar(255)      null comment '''策略描述''',    kind        tinyint           null comment '''策略类别-预留字段''',    status      tinyint default 1 not null comment '''2:disable 1:enable''',    alert_level tinyint           null comment '''预留字段''',    action      tinyint default 1 not null comment '''2:拦截 1:记录''',    next_action tinyint default 1 not null comment '''2:拦截 1:记录''');create index idx_waf_strategy_deleted_at    on waf_strategy (deleted_at);