create table waf_user_rule(    id           bigint unsigned auto_increment        primary key,    created_at   datetime(3)       null,    updated_at   datetime(3)       null,    deleted_at   datetime(3)       null,    name         varchar(255)      not null comment '''规则名称''',    description  varchar(255)      null comment '''规则描述''',    risk_level   tinyint default 1 not null comment '''风险等级''',    group_id     bigint            not null comment '''分组id''',    seclang      varchar(255)      not null comment '''安全规则''',    mod_security varchar(255)      not null comment '''安全规则语言''');create index idx_waf_user_rule_deleted_at    on waf_user_rule (deleted_at);