create table waf_allow_list(    id          bigint unsigned auto_increment        primary key,    created_at  datetime(3)  null,    updated_at  datetime(3)  null,    deleted_at  datetime(3)  null,    name        varchar(255) not null comment '''白名单昵称''',    description varchar(255) not null comment '''白名单描述''',    `key`       varchar(255) not null comment '''白名单匹配方式[IP , URI]''',    value       varchar(255) not null comment '''白名单匹配值''');create index idx_waf_allow_list_deleted_at    on waf_allow_list (deleted_at);