create table waf_buildin_rule(    id          bigint unsigned auto_increment        primary key,    created_at  datetime(3)       null,    updated_at  datetime(3)       null,    deleted_at  datetime(3)       null,    name        varchar(255)      not null comment '''内置规则名称''',    description varchar(255)      null comment '''内置规则描述''',    risk_level  tinyint default 1 not null comment '''风险等级''',    group_id    bigint            not null comment '''分组id''',    seclang     varchar(255)      not null comment '''安全规则''',    constraint uni_waf_buildin_rule_name        unique (name));create index idx_waf_buildin_rule_deleted_at    on waf_buildin_rule (deleted_at);