create table if not exists waf_rule_group(    id          bigint unsigned auto_increment        primary key,    created_at  datetime(3)  null,    updated_at  datetime(3)  null,    deleted_at  datetime(3)  null,    name        varchar(255) not null comment '''规则组名称''',    description varchar(255) null comment '''规则组描述''',    is_buildin  tinyint      null comment '''是否是内置规则组 1:是 2否''');create index idx_waf_rule_group_deleted_at    on waf_rule_group (deleted_at);