create table if not exists waf_servers_strategies(    id          bigint unsigned auto_increment        primary key,    created_at  datetime(3) null,    updated_at  datetime(3) null,    deleted_at  datetime(3) null,    server_id   bigint      not null comment '''服务器ID''',    strategy_id bigint      not null comment '''策略ID''');create index idx_waf_servers_strategies_deleted_at    on waf_servers_strategies (deleted_at);