create table if not exists waf_strategy_config(    id            bigint unsigned auto_increment        primary key,    created_at    datetime(3) null,    updated_at    datetime(3) null,    deleted_at    datetime(3) null,    strategy_id   bigint      not null comment '''策略id''',    rule_group_id bigint      not null comment '''规则组id''');create index idx_waf_strategy_config_deleted_at    on waf_strategy_config (deleted_at);