CREATE TABLE IF NOT EXISTS waf_user_rule (                                             id           BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,                                             created_at   DATETIME(3)       NULL,                                             updated_at   DATETIME(3)       NULL,                                             deleted_at   DATETIME(3)       NULL,                                             name         VARCHAR(255)      NOT NULL COMMENT '规则名称',                                             description  VARCHAR(255)      NULL COMMENT '规则描述',                                             risk_level   TINYINT DEFAULT 1 NOT NULL COMMENT '风险等级',                                             group_id     BIGINT            NOT NULL COMMENT '分组id',                                             seclang      VARCHAR(255)      NOT NULL COMMENT '安全规则',                                             mod_security VARCHAR(255)      NOT NULL COMMENT '安全规则语言',                                             INDEX idx_waf_user_rule_deleted_at (deleted_at)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;