CREATE TABLE IF NOT EXISTS app_waf (     id         BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,     created_at DATETIME(3)  NULL,     updated_at DATETIME(3)  NULL,     deleted_at DATETIME(3)  NULL,     name       VARCHAR(255) NOT NULL COMMENT '应用名称',      -- 修正注释语法（单引号）     url        VARCHAR(255) NULL COMMENT 'web程序地址',     server_id  BIGINT       NOT NULL COMMENT 'web程序所在服务器id',-- 直接在表定义中声明唯一约束和索引     UNIQUE KEY uni_app_waf_name (name),                    -- 唯一约束     UNIQUE KEY uni_app_waf_server_id (server_id),          -- 唯一约束     INDEX idx_app_waf_deleted_at (deleted_at)            -- 普通索引) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                   -- 可选：指定存储引擎和字符集