CREATE TABLE IF NOT EXISTS server_waf (    id              BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,    created_at      DATETIME(3)       NULL,    updated_at      DATETIME(3)       NULL,    deleted_at      DATETIME(3)       NULL,    name            VARCHAR(255)      NOT NULL COMMENT '服务器名称',    host            VARCHAR(255)      NOT NULL COMMENT '服务器域名',    ip              VARCHAR(255)      NOT NULL COMMENT '服务器IP地址',    port            BIGINT            NOT NULL COMMENT '服务器端口',    tls             TINYINT DEFAULT 2 NOT NULL COMMENT '是否开启TLS 2否 1是',    cert            VARCHAR(255)      NULL COMMENT 'TLS证书',    `key`           VARCHAR(255)      NULL COMMENT 'TLS密钥',    server_group_id BIGINT  DEFAULT 0 NOT NULL COMMENT '服务器组ID-预留字段',    UNIQUE KEY uni_server_waf_host (host),        -- 唯一约束    UNIQUE KEY uni_server_waf_name (name),        -- 唯一约束    INDEX idx_server_waf_deleted_at (deleted_at)  -- 普通索引) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;