CREATE TABLE IF NOT EXISTS waf_allow_list (                                              id          BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,                                              created_at  DATETIME(3)  NULL,                                              updated_at  DATETIME(3)  NULL,                                              deleted_at  DATETIME(3)  NULL,                                              name        VARCHAR(255) NOT NULL COMMENT '白名单昵称',                                              description VARCHAR(255) NOT NULL COMMENT '白名单描述',                                              `key`       VARCHAR(255) NOT NULL COMMENT '白名单匹配方式[IP, URI]',                                              value       VARCHAR(255) NOT NULL COMMENT '白名单匹配值',                                              INDEX idx_waf_allow_list_deleted_at (deleted_at)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;