CREATE TABLE IF NOT EXISTS waf_buildin_rule (                                                id          BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,                                                created_at  DATETIME(3)       NULL,                                                updated_at  DATETIME(3)       NULL,                                                deleted_at  DATETIME(3)       NULL,                                                name        VARCHAR(255)      NOT NULL COMMENT '内置规则名称',                                                description VARCHAR(255)      NULL COMMENT '内置规则描述',                                                risk_level  TINYINT DEFAULT 1 NOT NULL COMMENT '风险等级',                                                group_id    BIGINT            NOT NULL COMMENT '分组id',                                                seclang     VARCHAR(255)      NOT NULL COMMENT '安全规则',                                                UNIQUE KEY uni_waf_buildin_rule_name (name),        -- 唯一约束                                                INDEX idx_waf_buildin_rule_deleted_at (deleted_at)  -- 普通索引) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;