CREATE TABLE IF NOT EXISTS waf_rule_group (                                              id          BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,                                              created_at  DATETIME(3)  NULL,                                              updated_at  DATETIME(3)  NULL,                                              deleted_at  DATETIME(3)  NULL,                                              name        VARCHAR(255) NOT NULL COMMENT '规则组名称',                                              description VARCHAR(255) NULL COMMENT '规则组描述',                                              is_buildin  TINYINT      NULL COMMENT '是否是内置规则组 1:是 2否',                                              INDEX idx_waf_rule_group_deleted_at (deleted_at)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;