CREATE TABLE IF NOT EXISTS waf_servers_strategies (                                                      id          BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,                                                      created_at  DATETIME(3) NULL,                                                      updated_at  DATETIME(3) NULL,                                                      deleted_at  DATETIME(3) NULL,                                                      server_id   BIGINT      NOT NULL COMMENT '服务器ID',                                                      strategy_id BIGINT      NOT NULL COMMENT '策略ID',                                                      INDEX idx_waf_servers_strategies_deleted_at (deleted_at)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;