CREATE TABLE IF NOT EXISTS waf_strategy_config (                                                   id            BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,                                                   created_at    DATETIME(3) NULL,                                                   updated_at    DATETIME(3) NULL,                                                   deleted_at    DATETIME(3) NULL,                                                   strategy_id   BIGINT      NOT NULL COMMENT '策略id',                                                   rule_group_id BIGINT      NOT NULL COMMENT '规则组id',                                                   INDEX idx_waf_strategy_config_deleted_at (deleted_at)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;